package com.leetcode.oj.problem.solution.easy;

import org.junit.Assert;
import org.junit.Test;

/**
 * Created by leeyee on 2017/8/12.
 */
public class FindTheDifferenceTest {
    String s = "";
    String t = "";

    @Test
    public void findTheDifference() throws Exception {
        FindTheDifference fd = new FindTheDifference();

        Assert.assertEquals('a', fd.findTheDifference("a", "aa"));

        Assert.assertEquals('a', fd.findTheDifference("", "a"));
        Assert.assertEquals('e', fd.findTheDifference("abc", "bace"));
        Assert.assertEquals('t', fd.findTheDifference(s, t));
    }

    @Test
    public void findTheDifference1() throws Exception {
        FindTheDifference fd = new FindTheDifference();

        Assert.assertEquals('t', fd.findTheDifference1(s, t));

        Assert.assertEquals('a', fd.findTheDifference1("a", "aa"));

        Assert.assertEquals('a', fd.findTheDifference1("", "a"));
        Assert.assertEquals('e', fd.findTheDifference1("abc", "bace"));


    }

}