package com.leetcode.oj.problem.solution.easy;

import junit.framework.Assert;
import org.junit.Test;

/**
 * Created by wli on 2017-11-07.
 */
public class RepeatedSubstringPatternTest {
    @Test
    public void repeatedSubstringPattern() throws Exception {
        RepeatedSubstringPattern pattern = new RepeatedSubstringPattern();
        Assert.assertFalse(pattern.repeatedSubstringPattern("a"));
        Assert.assertFalse(pattern.repeatedSubstringPattern("ab"));
        Assert.assertFalse(pattern.repeatedSubstringPattern("aba"));
        Assert.assertTrue(pattern.repeatedSubstringPattern("abab"));
        Assert.assertTrue(pattern.repeatedSubstringPattern("aaaaaa"));
        Assert.assertTrue(pattern.repeatedSubstringPattern("abcabcabcabc"));
        Assert.assertTrue(pattern.repeatedSubstringPattern(""));
    }
}